var dataset = [];
for (var j = 0;j<data.length;j++){
    dataset[j] = [];
    dataset[j] = {"heights":[],"label":data[j][0]};
    var pts = [];
    for (var k=1;k<11;k++){
        if (data[j][k]==""){
            pts[k-1] = 0;
        }else{
            pts[k-1] = parseFloat(data[j][k]);
        }
    }

    dataset[j].heights = pts;
}

